import React, { useState, useEffect, useCallback } from "react";
import Header from '../../components/Common/Header';
import Banner from '../../components/Common/Banner';
import ProductInfo from './ProductInfo';
import RelatedProduct from './RelatedProduct';
import Footer from "../../components/Common/Footer";
import Slider from "react-slick";
import { Link, useParams } from 'react-router-dom';
import { useSelector, useDispatch } from "react-redux";
import { RatingStar } from "rating-star";
import { addToFavorites } from "../../../app/slices/product";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const ProductDetailsTwo = () => {
    const dispatch = useDispatch();
    const { id } = useParams();
    const product = useSelector((state) => state.products.single);
    const [count, setCount] = useState(1);
    const [img, setImg] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch product by ID
    useEffect(() => {
        const fetchProduct = async () => {
            try {
                dispatch({ type: "products/getProductById", payload: { id } });
            } catch (e) {
                setError(e);
            } finally {
                setLoading(false);
            }
        };
        fetchProduct();
    }, [dispatch, id]);

    // Update image based on color selection
    const colorSwatch = useCallback((color) => {
        const data = product?.color.find(item => item.color === color);
        if (data) setImg(data.img);
    }, [product]);

    // Increment quantity
    const incNum = () => setCount(prevCount => prevCount + 1);

    // Decrement quantity
    const decNum = () => {
        if (count > 1) {
            setCount(prevCount => prevCount - 1);
        } else {
            alert("Stokta Yok!");
            setCount(1);
        }
    };

    // Slider settings
    const settings = {
        arrows: true,
        dots: true,
        infinite: true,
        speed: 500,
        fade: true,
        autoplay: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                }
            }
        ]
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error loading product.</div>;

    return (
        <>
            <Header />
            <Banner title="Ürün Detay" />
            {product ? (
                <section id="product_single_two" className="ptb-100">
                    <div className="container">
                        <div className="row area_boxed">
                            <div className="col-lg-4">
                                <div className="product_single_two_img slider-for">
                                    <Slider {...settings}>
                                        <div className="product_img_two_slider">
                                            <img src={img || product.img} alt="Product" />
                                        </div>
                                        <div className="product_img_two_slider">
                                            <img src={product.hover_img} alt="Hover" />
                                        </div>
                                        {product.color.map(item => (
                                            <div className="product_img_two_slider" key={item.color}>
                                                <img src={item.img} alt={item.color} />
                                            </div>
                                        ))}
                                    </Slider>
                                </div>
                            </div>
                            <div className="col-lg-8">
                                <div className="product_details_right_one">
                                    <div className="modal_product_content_one">
                                        <h3>{product.title}</h3>
                                        <div className="reviews_rating">
                                            <RatingStar maxScore={5} rating={product.rating.rate} id="rating-star-common-2" />
                                            <span>({product.rating.count} Müşteri Yorumları)</span>
                                        </div>
                                        <h4>{product.price}.00 TL <del>{parseInt(product.price) + 17}.00 TL</del></h4>
                                        <p>{product.description}</p>
                                        <div className="customs_selects">
                                            <select name="product" className="customs_sel_box">
                                                <option value="">Beden</option>
                                                <option value="small">S</option>
                                                <option value="medium">M</option>
                                                <option value="large">L</option>
                                                <option value="xl">XL</option>
                                            </select>
                                        </div>
                                        <div className="variable-single-item">
                                            <span>Renk</span>
                                            <div className="product-variable-color">
                                                <label htmlFor="modal-product-color-red1">
                                                    <input name="modal-product-color" id="modal-product-color-red1"
                                                        className="color-select" type="radio" onChange={() => colorSwatch('red')} defaultChecked />
                                                    <span className="product-color-red"></span>
                                                </label>
                                                <label htmlFor="modal-product-color-green3">
                                                    <input name="modal-product-color" id="modal-product-color-green3"
                                                        className="color-select" type="radio" onChange={() => colorSwatch('green')} />
                                                    <span className="product-color-green"></span>
                                                </label>
                                                <label htmlFor="modal-product-color-blue5">
                                                    <input name="modal-product-color" id="modal-product-color-blue5"
                                                        className="color-select" type="radio" onChange={() => colorSwatch('blue')} />
                                                    <span className="product-color-blue"></span>
                                                </label>
                                            </div>
                                        </div>
                                        <form id="product_count_form_two">
                                            <div className="product_count_one">
                                                <div className="plus-minus-input">
                                                    <div className="input-group-button">
                                                        <button type="button" className="button" onClick={decNum}>
                                                            <i className="fa fa-minus"></i>
                                                        </button>
                                                    </div>
                                                    <input className="form-control" type="number" value={count} readOnly />
                                                    <div className="input-group-button">
                                                        <button type="button" className="button" onClick={incNum}>
                                                            <i className="fa fa-plus"></i>
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        </form>
                                        <div className="links_Product_areas">
                                            <ul>
                                                <li>
                                                    <a href="#!" className="action wishlist" title="Wishlist" onClick={() => addToFavorites(product.id)}>
                                                        <i className="fa fa-heart"></i>Favorilere Ekle
                                                    </a>
                                                </li>
                                            </ul>
                                            <a href="#!" className="theme-btn-one btn-black-overlay btn_sm"
                                                onClick={() => addToCart(product.id)}>Sepete Ekle</a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <ProductInfo />
                    </div>
                </section>
            ) : (
                <div className="container ptb-100">
                    <div className="row">
                        <div className="col-lg-6 offset-lg-3 col-md-6 offset-md-3 col-sm-12 col-12">
                            <div className="empty_cart_area">
                                <img src={img || '/path/to/placeholder-image.png'} alt="Not Found" />
                                <h2>Ürün Bulunamadı</h2>
                                <Link to="/shop" className="btn btn-black-overlay btn_sm">Alışverişe Devam</Link>
                            </div>
                        </div>
                    </div>
                </div>
            )}
            <RelatedProduct />
            <Footer />
        </>
    );
}

export default ProductDetailsTwo;
